struct pair_hash {
    template <class T1, class T2>
    size_t operator () (const pair<T1,T2> &p) const {
        auto h1 = hash<T1>{}(p.first);
        auto h2 = hash<T2>{}(p.second);
        return h1 ^ (h2 << 1);
    }
};

unordered_map<pair<int, int>, int, pair_hash> cell;
int cnt[6] = {0};

int val(int p, int x, int y) {
    switch (p) {
        case 0: return (x + y) % 2;
        case 1: return (x + y + 1) % 2;
        case 2: return (y - 1) % 2;
        case 3: return y % 2;
        case 4: return (x - 1) % 2;
        case 5: return x % 2;
        default: return 0;
    }
}

