            int old_t = cell[pos];
            for (int p = 0; p < 6; ++p) {
                int v = val(p, x, y);
                if (v != old_t) cnt[p]--;
            }
            cell.erase(pos);
        }

        if (t != -1) {
            cell[pos] = t;
            for (int p = 0; p < 6; ++p) {
                int v = val(p, x, y);
                if (v != t) cnt[p]++;
            }
        }

        int ans = 0;
        for (int p = 0; p < 6; ++p)
            if (cnt[p] == 0) ans++;
        cout << ans << '\n';
    }

