struct Event {
    int x, t;
    int a, b;
    bool validStarter;
};

struct FenwickTree {
    vector<int> tree;

    FenwickTree(int size) : tree(size + 1, 0) {}

    void update(int pos, int value) {
        while (pos < tree.size()) {
            if (tree[pos] < value) {
                tree[pos] = value;
                pos += pos & -pos;
            } else {
                break;
            }
        }
    }

    int query(int pos) {
        int max_val = 0;
        while (pos > 0) {
            max_val = max(max_val, tree[pos]);
            pos -= pos & -pos;
        }
        return max_val;
    }
};

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int N;
    cin >> N;
    vector<Event> events(N);
    for (int i = 0; i < N; ++i) {
        cin >> events[i].x >> events[i].t;
    }
    int V;
    cin >> V;

    vector<int> all_b;
    for (auto& e : events) {
        e.a = e.x - V * e.t;
        e.b = e.x + V * e.t;
        all_b.push_back(e.b);
        e.validStarter = (abs(e.x) <= V * e.t);
    }

    sort(events.begin(), events.end(), [](const Event& e1, const Event& e2) {
        if (e1.a != e2.a) return e1.a > e2.a;
        return e1.b < e2.b;
    });

    sort(all_b.begin(), all_b.end());
    all_b.erase(unique(all_b.begin(), all_b.end()), all_b.end());
    int m = all_b.size();

    vector<int> tails;
    for (const auto& e : events) {
        int b = e.b;
        auto it = upper_bound(tails.begin(), tails.end(), b);
        if (it == tails.end()) {
            tails.push_back(b);
        } else {
            *it = b;
        }
    }
    int part2 = tails.size();

    FenwickTree ft(m);
    int part1 = 0;
    for (const auto& e : events) {
        int pos = lower_bound(all_b.begin(), all_b.end(), e.b) - all_b.begin() + 1;
        int query_result = ft.query(pos);
        int current_dp = 0;

        if (e.validStarter) {
            current_dp = max(1, query_result + 1);
        } else if (query_result > 0) {
            current_dp = query_result + 1;
        }

        if (current_dp > 0) {
            ft.update(pos, current_dp);
            part1 = max(part1, current_dp);
        }
    }

    cout << part1 << " " << part2 << "\n";

    return 0;
}

