vector<int> get_centers(vector<vector<int>>& adj) {
    int n = adj.size() - 1;
    vector<int> degree(n + 1, 0);
    queue<int> q;
    for (int i = 1; i <= n; ++i) {
        degree[i] = adj[i].size();
        if (degree[i] == 1) {
            q.push(i);
            degree[i] = 0;
        }
    }
    int cnt = q.size();
    while (cnt < n) {
        int sz = q.size();
        for (int i = 0; i < sz; ++i) {
            int u = q.front(); q.pop();
            for (int v : adj[u]) {
                if (--degree[v] == 1) {
                    q.push(v);
                    degree[v] = 0;
                    cnt++;
                }
            }
        }
    }
    vector<int> centers;
    while (!q.empty()) {
        centers.push_back(q.front());
        q.pop();
    }
    return centers;
}

