// This problem requires a quantum computing solution, which is typically implemented in Q# rather than C++.
// Below is the Q# code that solves the problem as described.

namespace Solution {
    open Microsoft.Quantum.Intrinsic;
    open Microsoft.Quantum.Canon;

    operation Oracle(x : Qubit[], y : Qubit) : Unit is Adj + Ctl {
        let N = Length(x);
        if (N == 1) {
            X(y);
        } else {
            using (ancillas = Qubit[N - 1]) {
                // Compute XOR of each pair into ancillas
                for (i in 0..N - 2) {
                    CNOT(x[i], ancillas[i]);
                    CNOT(x[i + 1], ancillas[i]);
                }

                // Apply multi-controlled X on y when all ancillas are 1 (all pairs differ)
                Controlled X(ancillas, y);

                // Uncompute ancillas
                for (i in 0..N - 2) {
                    CNOT(x[i + 1], ancillas[i]);
                    CNOT(x[i], ancillas[i]);
                }
            }
        }
    }
}

