        if (t == 1 || t == 3) {
            cin >> s;
        }
        events.push_back({t, s});
    }

    vector<vector<int>> current_dp(301, vector<int>(2, INF));
    current_dp[0][1] = 0;  // Initial state: no speed limit, overtake allowed

    vector<vector<int>> next_dp(301, vector<int>(2, INF));

    for (const auto& e : events) {
        // Reset next_dp to INF
        for (auto& row : next_dp) {
            fill(row.begin(), row.end(), INF);
        }

        if (e.type == 1 || e.type == 2) {
            if (e.type == 1) {
                int new_speed = e.s;
                for (int s_prev = 0; s_prev <= 300; ++s_prev) {
                    for (int o_prev = 0; o_prev < 2; ++o_prev) {
                        if (current_dp[s_prev][o_prev] != INF) {
                            bool allowed = (s_prev == 0) || (new_speed <= s_prev);
                            if (allowed) {
                                next_dp[s_prev][o_prev] = min(next_dp[s_prev][o_prev], current_dp[s_prev][o_prev]);
                            }
                        }
                    }
                }
            } else { // type 2
                for (int s_prev = 0; s_prev <= 300; ++s_prev) {
                    for (int o_prev = 0; o_prev < 2; ++o_prev) {
                        if (current_dp[s_prev][o_prev] != INF) {
                            if (o_prev == 1) {
                                next_dp[s_prev][o_prev] = min(next_dp[s_prev][o_prev], current_dp[s_prev][o_prev]);
                            }
                        }
                    }
                }
            }
        } else {
            for (int s_prev = 0; s_prev <= 300; ++s_prev) {
                for (int o_prev = 0; o_prev < 2; ++o_prev) {
                    if (current_dp[s_prev][o_prev] != INF) {
                        // Option 1: ignore the sign
                        if (next_dp[s_prev][o_prev] > current_dp[s_prev][o_prev] + 1) {
                            next_dp[s_prev][o_prev] = current_dp[s_prev][o_prev] + 1;
                        }
                        // Option 2: take the sign
                        int new_s = s_prev;
                        int new_o = o_prev;
                        switch (e.type) {
                            case 3:
                                new_s = e.s;
                                break;
                            case 4:
                                new_o = 1;
                                break;
                            case 5:
                                new_s = 0;
                                break;
                            case 6:
                                new_o = 0;
                                break;
                        }
                        if (next_dp[new_s][new_o] > current_dp[s_prev][o_prev]) {
                            next_dp[new_s][new_o] = current_dp[s_prev][o_prev];
                        }
                    }
                }
            }
        }

        current_dp.swap(next_dp);
    }

    int result = INF;
    for (int s = 0; s <= 300; ++s) {
        for (int o = 0; o < 2; ++o) {
            result = min(result, current_dp[s][o]);
        }
    }

    cout << result << "\n";
    return 0;
}

#include <iostream>
