using namespace std;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;

    vector<int> x(n);
    for (int i = 0; i < n; ++i) {
        cin >> x[i];
    }

    vector<int> v(n);
    for (int i = 0; i < n; ++i) {
        cin >> v[i];
    }

    // Check if all friends are already at the same position
    bool all_same = true;
    for (int i = 1; i < n; ++i) {
        if (x[i] != x[0]) {
            all_same = false;
            break;
        }
    }
    if (all_same) {
        cout << fixed << setprecision(12) << 0.0 << '\n';
        return 0;
    }

    // Determine the range of positions
    int min_x = *min_element(x.begin(), x.end());
    int max_x = *max_element(x.begin(), x.end());

    // Calculate initial upper bound for binary search
    double upper = 0.0;
    for (int i = 0; i < n; ++i) {
        double time_min = static_cast<double>(x[i] - min_x) / v[i];
        double time_max = static_cast<double>(max_x - x[i]) / v[i];
        upper = max(upper, max(time_min, time_max));
    }

    double low = 0.0, high = upper;

    // Binary search to find the minimal time
    const int iterations = 100;
    for (int it = 0; it < iterations; ++it) {
        double mid = (low + high) * 0.5;
        double max_left = -1e300;
        double min_right = 1e300;

        for (int i = 0; i < n; ++i) {
            double left = x[i] - v[i] * mid;
            double right = x[i] + v[i] * mid;
            if (left > max_left) max_left = left;
            if (right < min_right) min_right = right;
        }

        if (max_left <= min_right) {
            high = mid;
        } else {
            low = mid;
        }
    }

    cout << fixed << setprecision(12) << high << '\n';

    return 0;
}

