#include <bits/stdc++.h>
#include "deliveries.h"
#include <bits/stdc++.h>

using namespace std;

vector<int> W;
long long sum_T = 0;
set<int> non_zero;
vector<long long> D;

void init(int N, vector<int> U, vector<int> V, vector<int> T, vector<int> W_init) {
    D.resize(N);
    D[0] = 0;
    for (int i = 1; i < N; ++i) {
        D[i] = D[i-1] + T[i-1];
    }

    W = W_init;
    sum_T = 0;
    for (int i = 0; i < N; ++i) {
        sum_T += W[i];
        if (W[i] > 0) {
            non_zero.insert(i);
        }
    }
}

long long max_time(int S, int X) {
    int old = W[S];
    sum_T += (X - old);
    if (old > 0) {
        non_zero.erase(S);
    }
    W[S] = X;
    if (X > 0) {
        non_zero.insert(S);
    }

    if (sum_T == 0) {
        return 0;
    }

    int L = *non_zero.begin();
    int R = *non_zero.rbegin();
    long long T_total = sum_T;

    long long ans;
    if (T_total % 2 == 0) {
        ans = (D[L] + D[R]) + (D[R] - D[L]) * (T_total - 1);
    } else {
        ans = 2 * D[R] + (D[R] - D[L]) * (T_total - 1);
    }

    return ans;
}

