#include <bits/stdc++.h>
using namespace std;

int main() {
    int n, m;
    cin >> n >> m;
    vector<vector<int>> adj(n + 1);
    for (int i = 0; i < m; ++i) {
        int a, b;
        cin >> a >> b;
        adj[a].push_back(b);
        adj[b].push_back(a);
    }

    vector<bool> visited(n + 1, false);
    vector<vector<pair<int, int>>> components;

    for (int i = 1; i <= n; ++i) {
        if (!visited[i]) {
            vector<int> component;
            queue<int> q;
            q.push(i);
            visited[i] = true;
            component.push_back(i);
            while (!q.empty()) {
                int u = q.front();
                q.pop();
                for (int v : adj[u]) {
                    if (!visited[v]) {
                        visited[v] = true;
                        component.push_back(v);
                        q.push(v);
                    }
                }
            }

            bool is_cycle = true;
            for (int u : component) {
                int cnt = 0;
                for (int v : adj[u]) {
                    if (find(component.begin(), component.end(), v) != component.end()) {
                        cnt++;
                    }
                }
                if (cnt != 2) {
                    is_cycle = false;
                    break;
                }
            }

            int sz = component.size();
            vector<pair<int, int>> opts;
            if (is_cycle) {
                if (sz % 2 == 0) opts.emplace_back(sz, 0);
                else opts.emplace_back(sz - 1, 0);
            } else {
                if (sz % 2 == 0) {
                    opts.emplace_back(sz, 0);
                } else {
                    opts.emplace_back(sz, 1);
                    opts.emplace_back(sz, -1);
                    opts.emplace_back(sz - 1, 0);
                }
            }
            components.push_back(opts);
        }
    }

    map<int, int> dp;
    dp[0] = 0;
    for (auto& comp : components) {
        map<int, int> new_dp = dp;
        for (auto [d, s] : dp) {
            for (auto [size, delta] : comp) {
                int new_d = d + delta;
                int new_s = s + size;
                if (new_s > new_dp[new_d]) {
                    new_dp[new_d] = new_s;
                }
            }
        }
        dp.swap(new_dp);
    }

    int max_size = 0;
    for (auto [delta, size] : dp) {
        if (delta == 0 && size % 2 == 0) {
            max_size = max(max_size, size);
        }
    }

    cout << n - max_size << endl;
    return 0;
}

