using ll = long long;
using pii = pair<int, int>;

const ll INF = 1e18;

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m, s;
    cin >> n >> m >> s;

    vector<vector<pii>> adj(n + 1);
    vector<tuple<int, int, int>> edges;
    edges.reserve(m);
    for (int i = 0; i < m; ++i) {
        int u, v, w;
        cin >> u >> v >> w;
        adj[u].emplace_back(v, w);
        adj[v].emplace_back(u, w);
        edges.emplace_back(u, v, w);
    }

    ll l;
    cin >> l;

    vector<ll> dist(n + 1, INF);
    dist[s] = 0;
    priority_queue<pair<ll, int>, vector<pair<ll, int>>, greater<>> pq;
    pq.emplace(0, s);

    while (!pq.empty()) {
        auto [d, u] = pq.top();
        pq.pop();
        if (d > dist[u]) continue;
        for (auto [v, w] : adj[u]) {
            if (dist[v] > dist[u] + w) {
                dist[v] = dist[u] + w;
                pq.emplace(dist[v], v);
            }
        }
    }

    int count_nodes = 0;
    for (int i = 1; i <= n; ++i) {
        if (dist[i] == l) {
            count_nodes++;
        }
    }

    int count_edges = 0;
    for (auto [u, v, w] : edges) {
        ll du = dist[u], dv = dist[v];
        ll sum = du + dv + w;
        if (2 * l == sum) {
            ll x = (dv + w - du);
            x /= 2;
            if (x > 0 && x < w) {
                count_edges++;
            }
        } else if (2 * l < sum) {
            ll x1 = l - du;
            if (x1 > 0 && x1 < w) {
                count_edges++;
            }
            ll x2 = dv + w - l;
            if (x2 > 0 && x2 < w) {
                count_edges++;
            }
        }
    }

    cout << count_nodes + count_edges << '\n';

    return 0;
}

