#include <bits/stdc++.h>
using namespace std;

struct SegmentTree {
    vector<vector<int>> st;

    SegmentTree(const vector<int>& ris) {
        int n = ris.size();
        st.resize(4 * n);
        function<void(int, int, int)> build = [&](int node, int l, int r) {
            if (l == r) {
                st[node].push_back(ris[l]);
                return;
            }
            int mid = (l + r) / 2;
            build(2 * node + 1, l, mid);
            build(2 * node + 2, mid + 1, r);
            st[node].resize(st[2 * node + 1].size() + st[2 * node + 2].size());
            merge(st[2 * node + 1].begin(), st[2 * node + 1].end(),
                  st[2 * node + 2].begin(), st[2 * node + 2].end(),
                  st[node].begin());
        };
        build(0, 0, n - 1);
    }

    int query(int node, int l, int r, int ql, int qr, int y) {
        if (qr < l || ql > r) return -1;
        if (ql <= l && r <= qr) {
            auto it = upper_bound(st[node].begin(), st[node].end(), y);
            if (it == st[node].begin()) return -1;
            return *prev(it);
        }
        int mid = (l + r) / 2;
        return max(query(2 * node + 1, l, mid, ql, qr, y),
                   query(2 * node + 2, mid + 1, r, ql, qr, y));
    }

    int queryRange(int ql, int qr, int y) {
        return query(0, 0, st.size() / 4 - 1, ql, qr, y);
    }
};

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;

    vector<pair<int, int>> ropes(m);
    for (int i = 0; i < m; ++i) {
        cin >> ropes[i].first >> ropes[i].second;
    }

    sort(ropes.begin(), ropes.end(), [](const pair<int, int>& a, const pair<int, int>& b) {
        return a.first < b.first;
    });

    vector<int> ris;
    for (const auto& r : ropes) {
        ris.push_back(r.second);
    }

    SegmentTree st(ris);

    int q;
    cin >> q;
    while (q--) {
        int x, y;
        cin >> x >> y;

        int current_upper = x;
        while (true) {
            int lo = 0, hi = m - 1, idx = -1;
            while (lo <= hi) {
                int mid = (lo + hi) / 2;
                if (ropes[mid].first <= current_upper) {
                    idx = mid;
                    lo = mid + 1;
                } else {
                    hi = mid - 1;
                }
            }
            if (idx == -1) break;

            int max_ri = st.queryRange(0, idx, y);
            if (max_ri == -1) break;
            if (max_ri > current_upper) {
                current_upper = max_ri;
            } else {
                break;
            }
        }

        cout << current_upper << '\n';
    }

    return 0;
}

