using namespace std;

const int MAXN = 4001;
int n;
vector<int> a;
vector<int> prefix;

int memo[MAXN][MAXN][2][2]; // [l][r][k][turn] where k can be 0 or 1 (k or k+1)
bool visited[MAXN][MAXN][2][2];

int get_sum(int l, int r) {
    return prefix[r + 1] - prefix[l];
}

// turn: 0 for Igor (left), 1 for Zhenya (right)
// prev_k is the required k for the current player
int solve(int l, int r, int prev_k, int turn) {
    if (l > r) return 0;
    if (prev_k == 0) {
        if (turn == 0) { // Igor's first move can be 1 or 2
            int take1 = get_sum(l, l) + solve(l + 1, r, 1, 1);
            if (l + 1 > r) return take1;
            int take2 = get_sum(l, l + 1) + solve(l + 2, r, 2, 1);
            return max(take1, take2);
        } else {
            return 0;
        }
    }
    int required_k = prev_k;
    if (required_k > (turn == 0 ? r - l + 1 : r - l + 1)) return 0; // no move possible

    int &res = memo[l][r][turn][required_k - 1];
    if (visited[l][r][turn][required_k - 1]) return res;
    visited[l][r][turn][required_k - 1] = true;

    int current = 0;
    if (turn == 0) { // Igor's turn (left)
        int best = -1e9;
        for (int take : {required_k, required_k + 1}) {
            if (l + take - 1 > r) continue;
            int sum = get_sum(l, l + take - 1);
            int next_l = l + take;
            int diff = sum + solve(next_l, r, take, 1);
            best = max(best, diff);
        }
        current = best;
    } else { // Zhenya's turn (right)
        int best = 1e9;
        for (int take : {required_k, required_k + 1}) {
            if (r - take + 1 < l) continue;
            int sum = get_sum(r - take + 1, r);
            int next_r = r - take;
            int diff = -sum + solve(l, next_r, take, 0);
            best = min(best, diff);
        }
        current = best;
    }
    return res = current;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);
    cout.tie(0);

    cin >> n;
    a.resize(n);
    prefix.resize(n + 1);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
        prefix[i + 1] = prefix[i] + a[i];
    }

    memset(visited, 0, sizeof(visited));
    cout << solve(0, n - 1, 0, 0) << endl;

    return 0;
}

