vector<long long> maxSlidingWindow(const vector<long long>& nums, int k) {
    deque<int> dq;
    vector<long long> res;
    for (int i = 0; i < nums.size(); ++i) {
        while (!dq.empty() && dq.front() < i - k + 1)
            dq.pop_front();
        while (!dq.empty() && nums[dq.back()] <= nums[i])
            dq.pop_back();
        dq.push_back(i);
        if (i >= k - 1)
            res.push_back(nums[dq.front()]);
    }
    return res;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, w;
    cin >> n >> w;

    vector<long long> ans(w + 2, 0);

    for (int i = 0; i < n; ++i) {
        int l;
        cin >> l;
        vector<long long> a(l);
        for (int j = 0; j < l; ++j)
            cin >> a[j];

        int L = w - l;

        vector<long long> prefix_max(l);
        prefix_max[0] = a[0];
        for (int j = 1; j < l; ++j)
            prefix_max[j] = max(prefix_max[j-1], a[j]);

        vector<long long> mid_max;
        if (L + 1 <= l) {
            mid_max = maxSlidingWindow(a, L + 1);
        }

        vector<long long> suffix_max(l + 1);
        suffix_max[l] = LLONG_MIN;
        for (int j = l - 1; j >= 0; --j)
            suffix_max[j] = max(suffix_max[j + 1], a[j]);

        for (int j = 1; j <= min(L, l); ++j)
            ans[j] += prefix_max[j - 1];

        int mid_start = L + 1;
        int mid_end = min(l, w);
        for (int j = mid_start; j <= mid_end; ++j) {
            if (j - L - 1 >= (int)mid_max.size())
                continue;
            ans[j] += mid_max[j - L - 1];
        }

        int suf_start = l + 1;
        int suf_end = w;
        for (int j = suf_start; j <= suf_end; ++j) {
            int start = j - L;
            if (start < 1)
                start = 1;
            if (start > l)
                continue;
            ans[j] += suffix_max[start - 1];
        }
    }

    for (int j = 1; j <= w; ++j) {
        cout << ans[j] << (j < w ? ' ' : '\n');
    }

    return 0;
}

