#include <bits/stdc++.h>
using namespace std;

const double EPS = 1e-9;

struct Point {
    double x, y;
    Point() {}
    Point(double x, double y) : x(x), y(y) {}
    bool operator<(const Point& other) const {
        if (fabs(x - other.x) > EPS) return x < other.x;
        return y < other.y;
    }
};

struct Segment {
    Point a, b;
    Segment(Point a, Point b) : a(a), b(b) {}
    bool is_vertical() const {
        return fabs(a.x - b.x) < EPS;
    }
    bool is_horizontal() const {
        return fabs(a.y - b.y) < EPS;
    }
    Point closest_point_to(const Point& p) const {
        if (is_vertical()) {
            double min_y = min(a.y, b.y);
            double max_y = max(a.y, b.y);
            double y = max(min(p.y, max_y), min_y);
            return Point(a.x, y);
        } else {
            double min_x = min(a.x, b.x);
            double max_x = max(a.x, b.x);
            double x = max(min(p.x, max_x), min_x);
            return Point(x, a.y);
        }
    }
};

double distance(const Point& a, const Point& b) {
    double dx = a.x - b.x;
    double dy = a.y - b.y;
    return sqrt(dx*dx + dy*dy);
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);

    int a, b;
    cin >> a >> b;

    Point A, B;
    cin >> A.x >> A.y >> B.x >> B.y;

    int n;
    cin >> n;

    vector<Segment> trenches;
    for (int i = 0; i < n; ++i) {
        double x1, y1, x2, y2;
        cin >> x1 >> y1 >> x2 >> y2;
        trenches.emplace_back(Point(x1, y1), Point(x2, y2));
    }

    if (distance(A, B) < EPS) {
        printf("%.10f\n", 0.0);
        return 0;
    }

    priority_queue<pair<double, Point>, vector<pair<double, Point>>, greater<>> pq;
    pq.emplace(0.0, A);

    map<Point, double> visited;

    double ans = -1.0;

    while (!pq.empty()) {
        auto [current_time, current_point] = pq.top();
        pq.pop();

        if (distance(current_point, B) < EPS) {
            ans = current_time;
            break;
        }

        if (visited.count(current_point) && visited[current_point] <= current_time + EPS) continue;
        visited[current_point] = current_time;

        int cycle = a + b;
        double phase_time = fmod(current_time, cycle);
        bool is_charging = phase_time < a - EPS;

        if (is_charging) {
            int k = static_cast<int>(current_time / cycle);
            double s = k * cycle + a;
            double available_time = s - current_time;

            double d_to_B = distance(current_point, B);
            if (d_to_B <= available_time + EPS) {
                pq.emplace(current_time + d_to_B, B);
            }

            for (const auto& trench : trenches) {
                Point closest = trench.closest_point_to(current_point);
                double d = distance(current_point, closest);
                if (d > available_time + EPS) continue;

                double arrival_time = current_time + d;
                pq.emplace(arrival_time + b, trench.a);
                pq.emplace(arrival_time + b, trench.b);
            }
        } else {
            for (const auto& trench : trenches) {
                Point closest = trench.closest_point_to(current_point);
                if (distance(current_point, closest) > EPS) continue;

                double k = floor(current_time / cycle);
                double new_time = k * cycle + a + b;
                pq.emplace(new_time, trench.a);
                pq.emplace(new_time, trench.b);
                break;
            }
        }
    }

    if (ans < 0) {
        cout << "-1\n";
    } else {
        printf("%.10f\n", ans);
    }

    return 0;
}

