string res = "none";
vector<int> s;
int n;

void dfs(int pos, int sum_mod, string current) {
    if (pos == n) {
        if (sum_mod % n == 0) {
            if (res == "none" || current < res) {
                res = current;
            }
        }
        return;
    }
    for (char c = 'a'; c <= 'z'; ++c) {
        int shift = (c - 'a' - s[pos] + 26) % 26;
        int new_sum = (sum_mod + shift) % n;
        if (res != "none" && current + c >= res) break;
        dfs(pos + 1, new_sum, current + c);
    }
}

