#include <iostream>
#include <cmath>
#include <algorithm>
using namespace std;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;

    int d0_count = min(static_cast<int>(sqrt(m)), n);
    long long total_d_plus_roots = 0;

    for (int b = 1; b <= n; ++b) {
        long long b_sq = static_cast<long long>(b) * b;
        long long lower_k = max(1LL, b_sq - m);
        long long upper_k = min(b_sq - 1, static_cast<long long>(m));
        if (lower_k > upper_k) continue;
        total_d_plus_roots += 2 * (upper_k - lower_k + 1);
    }

    long long overlaps_x1 = 0;
    int t_max = (m - 1) / 2;
    for (int t = 1; t <= t_max; ++t) {
        long long val = m + static_cast<long long>(t) * t;
        long long B = sqrt(val);
        while (B * B > val) B--;
        while ((B + 1) * (B + 1) <= val) B++;
        long long upper_b = min(B, t + d0_count);
        if (upper_b >= t + 1)
            overlaps_x1 += (upper_b - t);
    }

    long long overlaps_x2 = 0;
    int t_max2 = (d0_count - 1) / 2;
    for (int t = 1; t <= t_max2; ++t) {
        long long val = m + static_cast<long long>(t) * t;
        long long B = sqrt(val);
        while (B * B > val) B--;
        while ((B + 1) * (B + 1) <= val) B++;
        long long upper_b = min(B, min(static_cast<long long>(d0_count) - t, static_cast<long long>(n)));
        long long lower_b = t + 1;
        if (upper_b >= lower_b)
            overlaps_x2 += (upper_b - lower_b + 1);
    }

    long long total = d0_count + (total_d_plus_roots - overlaps_x1 - overlaps_x2);
    cout << total << '\n';

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

vector<int> color;
vector<vector<int>> adj;
int conflict_u = -1, conflict_v = -1;

bool is_bipartite(int start, vector<int>& color_vec) {
    queue<int> q;
    q.push(start);
    color_vec[start] = 0;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : adj[u]) {
            if (color_vec[v] == -1) {
                color_vec[v] = color_vec[u] ^ 1;
                q.push(v);
            } else if (color_vec[v] == color_vec[u]) {
                conflict_u = u;
                conflict_v = v;
                return false;
            }
        }
    }
    return true;
}

bool check_remaining_bipartite(int n, int exclude1, int exclude2, vector<int>& part) {
    vector<int> new_color(n + 1, -1);
    for (int i = 1; i <= n; ++i) {
        if (i == exclude1 || i == exclude2) continue;
        if (new_color[i] == -1) {
            queue<int> q;
            q.push(i);
            new_color[i] = 0;
            while (!q.empty()) {
                int u = q.front();
                q.pop();
                for (int v : adj[u]) {
                    if (v == exclude1 || v == exclude2) continue;
                    if (new_color[v] == -1) {
                        new_color[v] = new_color[u] ^ 1;
                        q.push(v);
                    } else if (new_color[v] == new_color[u]) {
                        return false;
                    }
                }
            }
        }
    }
    part = new_color;
    return true;
}

void solve() {
    int n, m;
    cin >> n >> m;
    adj.assign(n + 1, {});
    for (int i = 0; i < m; ++i) {
        int u, v;
        cin >> u >> v;
        adj[u].push_back(v);
        adj[v].push_back(u);
    }

    color.assign(n + 1, -1);
    bool bipart = true;
    for (int i = 1; i <= n; ++i) {
        if (color[i] == -1) {
            if (!is_bipartite(i, color)) {
                bipart = false;
                break;
            }
        }
    }

    if (bipart) {
        cout << "YES\n";
        for (int i = 1; i <= n; ++i) {
            cout << (color[i] == 0 ? '1' : '0');
        }
        cout << '\n';
        return;
    }

    int u = conflict_u, v = conflict_v;
    vector<int> part_remaining;
    if (check_remaining_bipartite(n, u, v, part_remaining)) {
        string ans(n, '0');
        ans[u - 1] = '1';
        ans[v - 1] = '1';
        for (int i = 1; i <= n; ++i) {
            if (i == u || i == v) continue;
            if (part_remaining[i] == 0) {
                ans[i - 1] = '1';
            }
        }

        bool valid = true;
        int edge_count = 0;
        for (int a = 1; a <= n; ++a) {
            if (ans[a - 1] == '0') continue;
            for (int b : adj[a]) {
                if (a > b) continue;
                if (ans[b - 1] == '1') {
                    ++edge_count;
                }
            }
        }
        if (edge_count > 1) valid = false;

        if (valid) {
            cout << "YES\n" << ans << '\n';
            return;
        }
    }

    cout << "NO\n";
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    int t;
    cin >> t;
    while (t--) {
        solve();
    }
    return 0;
}

