struct State {
    int x1, y1, x2, y2;
    State(int a, int b, int c, int d) : x1(a), y1(b), x2(c), y2(d) {}
    bool operator==(const State& other) const {
        return x1 == other.x1 && y1 == other.y1 && x2 == other.x2 && y2 == other.y2;
    }
};

namespace std {
    template<> struct hash<State> {
        size_t operator()(const State& s) const {
            return (s.x1 << 24) | (s.y1 << 16) | (s.x2 << 8) | s.y2;
        }
    };
}

void construct_instructions(std::vector<std::vector<bool>> g, int q, int a, int b, int c, int d) {
    queue<State> bfs_queue;
    unordered_map<State, pair<State, char>> prev;

    State initial(a, b, c, d);
    State goal(0, 0, 0, 0);

    bfs_queue.push(initial);
    prev[initial] = make_pair(State(-1, -1, -1, -1), ' ');

    bool found = false;

    while (!bfs_queue.empty()) {
        State current = bfs_queue.front();
        bfs_queue.pop();

        if (current.x1 == 0 && current.y1 == 0 && current.x2 == 0 && current.y2 == 0) {
            found = true;
            break;
        }

        vector<char> directions = {'U', 'D', 'L', 'R'};
        for (char dir : directions) {
            int nx1 = current.x1, ny1 = current.y1;
            int nx2 = current.x2, ny2 = current.y2;

            switch (dir) {
                case 'U':
                    if (nx1 > 0 && !g[nx1 - 1][ny1]) nx1--;
                    break;
                case 'D':
                    if (nx1 < (int)g.size() - 1 && !g[nx1 + 1][ny1]) nx1++;
                    break;
                case 'L':
                    if (ny1 > 0 && !g[nx1][ny1 - 1]) ny1--;
                    break;
                case 'R':
                    if (ny1 < (int)g[0].size() - 1 && !g[nx1][ny1 + 1]) ny1++;
                    break;
            }

            switch (dir) {
                case 'U':
                    if (nx2 > 0 && !g[nx2 - 1][ny2]) nx2--;
                    break;
                case 'D':
                    if (nx2 < (int)g.size() - 1 && !g[nx2 + 1][ny2]) nx2++;
                    break;
                case 'L':
                    if (ny2 > 0 && !g[nx2][ny2 - 1]) ny2--;
                    break;
                case 'R':
                    if (ny2 < (int)g[0].size() - 1 && !g[nx2][ny2 + 1]) ny2++;
                    break;
            }

            State next(nx1, ny1, nx2, ny2);
            if (prev.find(next) == prev.end()) {
                prev[next] = make_pair(current, dir);
                bfs_queue.push(next);
            }
        }
    }

    vector<char> path;
    State current = goal;
    while (!(current == initial)) {
        auto it = prev.find(current);
        if (it == prev.end()) return;
        char dir = it->second.second;
        path.push_back(dir);
        current = it->second.first;
    }

    reverse(path.begin(), path.end());

    for (char dir : path) {
        switch (dir) {
            case 'U': up(); break;
            case 'D': down(); break;
            case 'L': left(); break;
            case 'R': right(); break;
        }
    }
}

