    pair<int, int> a, b;
};

void print_grid(const vector<vector<char>>& grid) {
    for (const auto& row : grid) {
        for (char c : row) {
            cout << c;
        }
        cout << '\n';
    }
}

bool construct_horizontal(int n, int m, int k, vector<vector<char>>& grid) {
    vector<Domino> dominoes;
    grid = vector<vector<char>>(n, vector<char>(m, ' '));
    int cnt = 0;
    char current_char = 'a';
    for (int i = 0; i < n && cnt < k; ++i) {
        for (int j = 0; j + 1 < m && cnt < k; j += 2) {
            grid[i][j] = grid[i][j + 1] = current_char;
            current_char = (current_char == 'a' ? 'b' : 'a');
            cnt++;
        }
    }
    if (cnt < k) return false;

    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            if (grid[i][j] == ' ') {
                if (i + 1 >= n || grid[i][j] != ' ' || grid[i + 1][j] != ' ') return false;
                char c = 'c' + (j % 2);
                if ((i / 2) % 2 == 1) c += 2;
                grid[i][j] = grid[i + 1][j] = c;
            }
        }
    }
    return true;
}

bool construct_vertical(int n, int m, int k, vector<vector<char>>& grid) {
    grid = vector<vector<char>>(n, vector<char>(m, ' '));
    int required = m / 2;
    if (k < required || (k - required) % 1 != 0) return false;

    for (int i = 0; i < n; ++i) {
        int placed = 0;
        for (int j = 0; j + 1 < m && placed < required; j += 2) {
            grid[i][j] = grid[i][j + 1] = (i % 2 == 0 ? 'a' : 'b');
            placed++;
        }
        k -= placed;
    }

    int additional = k;
    for (int i = 0; i < n && additional > 0; ++i) {
        for (int j = 0; j + 1 < m && additional > 0; j += 2) {
            if (grid[i][j] == ' ') {
                grid[i][j] = grid[i][j + 1] = (i % 2 == 0 ? 'c' : 'd');
                additional--;
            }
        }
    }

    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            if (grid[i][j] == ' ') {
                if (i + 1 >= n || grid[i][j] != ' ' || grid[i + 1][j] != ' ') return false;
                grid[i][j] = grid[i + 1][j] = 'e' + (j % 2);
            }
        }
    }
    return true;
}

void solve() {
    int n, m, k;
    cin >> n >> m >> k;
    int total = n * m / 2;
    if (k > total) {
        cout << "NO\n";
        return;
    }

    vector<vector<char>> grid;
    bool possible = false;

    if (n % 2 == 0) {
        int max_h = n * (m / 2);
        if (k > max_h || k < 0) {
            cout << "NO\n";
            return;
        }
        if (construct_horizontal(n, m, k, grid)) {
            possible = true;
        }
    } else {
        if (m % 2 != 0) {
            cout << "NO\n";
            return;
        }
        int min_k = m / 2;
        int max_k = n * (m / 2);
        if (k < min_k || k > max_k || (k - min_k) % 1 != 0) {
            cout << "NO\n";
            return;
        }
        if (construct_vertical(n, m, k, grid)) {
            possible = true;
        }
    }

    if (possible) {
        cout << "YES\n";
        print_grid(grid);
    } else {
        cout << "NO\n";
    }
}

