vector<int> find_routers(int l, int n, int q) {
    vector<int> p(n);
    p[0] = 0;

    for (int i = 1; i < n; ++i) {
        int low = p[i-1];
        int high = l;

        while (low < high) {
            int mid = (low + high + 1) / 2;
            int res = use_detector(mid);

            if (res == i-1) {
                low = mid;
            } else {
                high = mid - 1;
            }
        }

