vector<vector<int>> adj;
int global_N;

void init(int N, int S, vector<int> T, vector<vector<int>> A, vector<vector<int>> B) {
    global_N = N;
    adj.clear();
    adj.resize(N);

    vector<vector<pair<int, int>>> intervals(N);
    for (int u = 0; u < N; ++u) {
        int t = T[u];
        for (int j = 0; j < t; ++j) {
            int a = A[u][j];
            int b = B[u][j];
            intervals[u].emplace_back(a, b);
        }
    }

    for (int u = 0; u < N; ++u) {
        for (int v = u + 1; v < N; ++v) {
            bool has_overlap = false;
            for (const auto& iu : intervals[u]) {
                for (const auto& iv : intervals[v]) {
                    int a1 = iu.first, b1 = iu.second;
                    int a2 = iv.first, b2 = iv.second;
                    if (a1 <= b2 && a2 <= b1) {
                        has_overlap = true;
                        break;
                    }
                }
                if (has_overlap) break;
            }
            if (has_overlap) {
                adj[u].push_back(v);
                adj[v].push_back(u);
            }
        }
    }
}

int count_users(int P) {
    int N = global_N;
    vector<bool> visited(N, false);
    queue<pair<int, int>> q;
    int count = 0;

    q.emplace(P, 0);
    visited[P] = true;
    count = 1;

    while (!q.empty()) {
        auto [u, depth] = q.front();
        q.pop();

        for (int v : adj[u]) {
            if (!visited[v] && depth + 1 <= N) {
                visited[v] = true;
                count++;
                q.emplace(v, depth + 1);
            }
        }
    }

    return count;
}

