#include <bits/stdc++.h>
using namespace std;

struct Line {
    int a, b, c;

    void normalize() {
        int g = gcd(gcd(abs(a), abs(b)), abs(c));
        a /= g;
        b /= g;
        c /= g;

        int sign = 1;
        if (a != 0) {
            sign = a > 0 ? 1 : -1;
        } else if (b != 0) {
            sign = b > 0 ? 1 : -1;
        } else {
            sign = c > 0 ? 1 : -1;
        }

        a *= sign;
        b *= sign;
        c *= sign;
    }

    bool operator<(const Line& other) const {
        if (a != other.a) return a < other.a;
        if (b != other.b) return b < other.b;
        return c < other.c;
    }
};

int main() {
    int n;
    cin >> n;
    vector<pair<int, int>> points(n);
    for (auto& p : points) cin >> p.first >> p.second;

    set<Line> lines;
    for (int i = 0; i < n; ++i) {
        for (int j = i + 1; j < n; ++j) {
            int x1 = points[i].first, y1 = points[i].second;
            int x2 = points[j].first, y2 = points[j].second;
            Line line = {y2 - y1, x1 - x2, x2 * y1 - x1 * y2};
            line.normalize();
            lines.insert(line);
        }
    }

    map<pair<int, int>, int> dir_count;
    for (const Line& line : lines) {
        dir_count[{line.a, line.b}]++;
    }

    int m = lines.size();
    int total = m * (m - 1) / 2;
    for (const auto& [_, cnt] : dir_count) {
        total -= cnt * (cnt - 1) / 2;
    }

    cout << total << endl;
    return 0;
}

