#include <bits/stdc++.h>
#include "soccer.h"
#include <vector>
#include <algorithm>
#include <stack>

using namespace std;

int biggest_stadium(int N, vector<vector<int>> F) {
    // Calculate all completely empty rows
    vector<int> empty_rows;
    for (int r = 0; r < N; ++r) {
        bool empty = true;
        for (int c = 0; c < N; ++c) {
            if (F[r][c] == 1) {
                empty = false;
                break;
            }
        }
        if (empty) {
            empty_rows.push_back(r);
        }
    }

    // Calculate all completely empty columns
    vector<int> empty_cols;
    for (int c = 0; c < N; ++c) {
        bool empty = true;
        for (int r = 0; r < N; ++r) {
            if (F[r][c] == 1) {
                empty = false;
                break;
            }
        }
        if (empty) {
            empty_cols.push_back(c);
        }
    }

    // Calculate the size for the union of empty rows and columns
    int R = empty_rows.size();
    int C = empty_cols.size();
    int size_rows_cols = R * N + C * (N - R);

    // Calculate the largest rectangle of empty cells using the histogram method
    vector<int> heights(N, 0);
    int max_rect = 0;
    for (int r = 0; r < N; ++r) {
        // Update the heights for this row
        for (int c = 0; c < N; ++c) {
            if (F[r][c] == 0) {
                heights[c]++;
            } else {
                heights[c] = 0;
            }
        }

        // Use a stack to find the largest rectangle in the histogram
        stack<int> s;
        for (int c = 0; c < N; ++c) {
            while (!s.empty() && heights[c] < heights[s.top()]) {
                int h = heights[s.top()];
                s.pop();
                int w = s.empty() ? c : c - s.top() - 1;
                max_rect = max(max_rect, h * w);
            }
            s.push(c);
        }
        // Process remaining elements in the stack
        while (!s.empty()) {
            int h = heights[s.top()];
            s.pop();
            int w = s.empty() ? N : N - s.top() - 1;
            max_rect = max(max_rect, h * w);
        }
    }

    // The answer is the maximum between the two approaches
    return max(size_rows_cols, max_rect);
}

