        deg[a]++;
        deg[b]++;
    }
    long long sum_c = 0;
    for (int i = 1; i <= n; ++i) {
        long long d = deg[i];
        sum_c += d * (d - 1) / 2;
    }
    long long total = n >= 3 ? (long long)n * (n - 1) * (n - 2) / 6 : 0;
    long long ans = total - m * (n - 2LL) + sum_c;
    cout << ans << '\n';
    return 0;
}

