#include <bits/stdc++.h>
using namespace std;

bool solve(int a, int b, int c, int d, vector<int>& res) {
    if (abs((a + c) - (b + d)) > 1) return false;
    if (a > b + 1 || d > c + 1) return false;

    if (a > b) {
        for (int i = 0; i < b; ++i) {
            res.push_back(0);
            res.push_back(1);
        }
        res.push_back(0);
        for (int i = 0; i < c; ++i) {
            res.push_back(1);
            res.push_back(2);
        }
        if (d > 0) {
            res.push_back(3);
            d--;
            for (int i = 0; i < d; ++i) {
                res.push_back(2);
                res.push_back(3);
            }
        }
    } else if (d > c) {
        for (int i = 0; i < c; ++i) {
            res.push_back(3);
            res.push_back(2);
        }
        res.push_back(3);
        for (int i = 0; i < b; ++i) {
            res.push_back(2);
            res.push_back(1);
        }
        if (a > 0) {
            res.push_back(0);
            a--;
            for (int i = 0; i < a; ++i) {
                res.push_back(1);
                res.push_back(0);
            }
        }
    } else {
        for (int i = 0; i < a; ++i) {
            res.push_back(0);
            res.push_back(1);
        }
        for (int i = 0; i < min(b - a, c - d); ++i) {
            res.push_back(2);
            res.push_back(1);
        }
        for (int i = 0; i < d; ++i) {
            res.push_back(2);
            res.push_back(3);
        }
    }

    for (int i = 1; i < res.size(); ++i) {
        if (abs(res[i] - res[i-1]) != 1) return false;
    }

    vector<int> cnt(4, 0);
    for (int num : res) cnt[num]++;
    if (cnt[0] != a || cnt[1] != b || cnt[2] != c || cnt[3] != d) return false;

    return true;
}

int main() {
    int a, b, c, d;
    cin >> a >> b >> c >> d;

    vector<int> res;
    if (solve(a, b, c, d, res)) {
        cout << "YES\n";
        for (int num : res) cout << num << " ";
        cout << "\n";
    } else {
        cout << "NO\n";
    }

    return 0;
}

