#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
const int MOD = 998244353;

int n, m;
vector<int> c;
vector<vector<ll>> dp;

ll solve(int l, int r) {
    if (l > r) return 1;
    if (dp[l][r] != -1) return dp[l][r];

    int mn = n + 1;
    for (int i = l; i <= r; i++) {
        mn = min(mn, c[i]);
    }

    int first = -1, last = -1;
    for (int i = l; i <= r; i++) {
        if (c[i] == mn) {
            if (first == -1) first = i;
            last = i;
        }
    }

    ll ans = 0;
    for (int i = first; i <= last; i++) {
        if (c[i] == mn) {
            ans = (ans + (solve(l, first - 1) * solve(first, i - 1) % MOD) * solve(i + 1, last) % MOD * solve(last + 1, r) % MOD) % MOD;
        }
    }

    return dp[l][r] = ans;
}

int main() {
