using namespace std;

int find_maximum_unique(int x, int y, vector<int> a, vector<int> b) {
    int n = a.size();
    const int INF = INT_MIN;

    vector<vector<int>> current(x + 1, vector<int>(y + 1, INF));
    current[0][0] = 0;

    for (int i = 0; i < n; ++i) {
        vector<vector<int>> next = current;

        for (int sa = 0; sa <= x; ++sa) {
            for (int sb = 0; sb <= y; ++sb) {
                if (current[sa][sb] == INF) continue;

                // Try taking from Store A
                int new_sa = sa + a[i];
                int new_sb = sb;
                if (new_sa <= x && new_sb <= y) {
                    if (next[new_sa][new_sb] < current[sa][sb] + 1) {
                        next[new_sa][new_sb] = current[sa][sb] + 1;
                    }
                }

                // Try taking from Store B
                new_sa = sa;
                new_sb = sb + b[i];
                if (new_sa <= x && new_sb <= y) {
                    if (next[new_sa][new_sb] < current[sa][sb] + 1) {
                        next[new_sa][new_sb] = current[sa][sb] + 1;
                    }
                }
            }
        }

        current = move(next);
    }

    int max_count = 0;
    for (int sa = 0; sa <= x; ++sa) {
        for (int sb = 0; sb <= y; ++sb) {
            if (current[sa][sb] > max_count) {
                max_count = current[sa][sb];
            }
        }
    }

    return max_count;
}

