int main() {
    cin >> n;
    if (n == 1) {
        cout << "!\n";
        return 0;
    }

    int D;
    cout << "? 1";
    for (int i = 1; i < n-1; ++i) cout << " 1";
    cout << endl;
    cin >> D;

    vector<bool> in_chain(n, false);
    for (int e = 1; e <= n-1; ++e) {
        cout << "? 1";
        for (int i = 1; i <= n-1; ++i) {
            if (i == e) cout << " " << (D + 1);
            else cout << " 1";
        }
        cout << endl;
        int newD;
        cin >> newD;
        if (newD > D) {
            in_chain[e-1] = true;
            chain.push_back(e);
        }
    }

    // Now chain contains the edges in the diameter path
    // Need to arrange them in order
    if (chain.empty()) {
        chain.push_back(1);
        in_chain[0] = true;
    }

    // Select the first edge in chain as root
    int root = chain[0];
    vector<pair<int, int>> dists;
    for (int e : chain) {
        if (e == root) {
            dists.emplace_back(0, e);
            continue;
        }
        cout << "? 2 " << root << " " << e << endl;
        int d;
        cin >> d;
        dists.emplace_back(d, e);
    }

    sort(dists.begin(), dists.end());
    vector<int> sorted_chain;
    for (auto& p : dists) sorted_chain.push_back(p.second);

    // Now build the tree: sorted_chain is ordered as the chain
    vector<pair<int, int>> edges;
    for (int i = 1; i < sorted_chain.size(); ++i) {
        edges.emplace_back(sorted_chain[i-1], sorted_chain[i]);
    }

    parent.resize(n-1, -1);
    vector<int> is_processed(n-1, false);
    for (int e : sorted_chain) is_processed[e-1] = true;

    for (int e = 1; e <= n-1; ++e) {
        if (is_processed[e-1]) continue;
        bool found = false;
        for (int c : sorted_chain) {
            cout << "? 2 " << e << " " << c << endl;
            int d;
            cin >> d;
            if (d == 0) {
                edges.emplace_back(e, c);
                found = true;
                break;
            }
        }
        if (!found) edges.emplace_back(e, sorted_chain[0]);
    }

    cout << "!\n";
    int node = 1;
    vector<int> mapping(n+1, 0);
    mapping[edges[0].first] = 1;
    mapping[edges[0].second] = 2;
    cout << "1 2\n";
    node = 3;
    for (size_t i = 1; i < edges.size(); ++i) {
        int a = edges[i].first;
        int b = edges[i].second;
        if (mapping[a] == 0 && mapping[b] == 0) {
            mapping[a] = node++;
            mapping[b] = node++;
        } else if (mapping[a] == 0) {
            mapping[a] = node++;
        } else if (mapping[b] == 0) {
            mapping[b] = node++;
        }
        cout << mapping[a] << " " << mapping[b] << "\n";
    }

    return 0;
}

