#include <bits/stdc++.h>
using namespace std;

typedef long long ll;

int main() {
    int n;
    cin >> n;

    vector<tuple<int, int, int, int>> doors(n + 1);
    for (int i = 1; i <= n; ++i) {
        int a, b, c, d;
        cin >> a >> b >> c >> d;
        doors[i] = make_tuple(a, b, c, d);
    }

    int q;
    cin >> q;

    for (int i = 0; i < q; ++i) {
        int r1, c1, r2, c2;
        cin >> r1 >> c1 >> r2 >> c2;

        int layer1 = max(r1, c1);
        int layer2 = max(r2, c2);

        if (layer1 == layer2) {
            cout << abs(r1 - r2) + abs(c1 - c2) << '\n';
            continue;
        }

        vector<vector<int>> dist(n + 1, vector<int>(2, 1e9));
        dist[layer1][0] = abs(r1 - get<0>(doors[layer1])) + abs(c1 - get<1>(doors[layer1]));
        dist[layer1][1] = abs(r1 - get<2>(doors[layer1])) + abs(c1 - get<3>(doors[layer1]));

        priority_queue<tuple<int, int, int>, vector<tuple<int, int, int>>, greater<tuple<int, int, int>>> pq;
        pq.emplace(dist[layer1][0], layer1, 0);
        pq.emplace(dist[layer1][1], layer1, 1);

        while (!pq.empty()) {
            auto [d, layer, door_idx] = pq.top();
            pq.pop();

            if (d > dist[layer][door_idx]) continue;

            if (layer < n) {
                int door_r = (door_idx == 0) ? get<0>(doors[layer]) : get<2>(doors[layer]);
                int door_c = (door_idx == 0) ? get<1>(doors[layer]) : get<3>(doors[layer]);

                int next_door_r1 = get<0>(doors[layer + 1]);
                int next_door_c1 = get<1>(doors[layer + 1]);
                int next_door_r2 = get<2>(doors[layer + 1]);
                int next_door_c2 = get<3>(doors[layer + 1]);

                int new_dist1 = d + 1 + abs(door_r - next_door_r1) + abs(door_c - next_door_c1);
                int new_dist2 = d + 1 + abs(door_r - next_door_r2) + abs(door_c - next_door_c2);

                if (new_dist1 < dist[layer + 1][0]) {
                    dist[layer + 1][0] = new_dist1;
                    pq.emplace(new_dist1, layer + 1, 0);
                }
                if (new_dist2 < dist[layer + 1][1]) {
                    dist[layer + 1][1] = new_dist2;
                    pq.emplace(new_dist2, layer + 1, 1);
                }
            }

            if (layer > 1) {
                 int door_r = (door_idx == 0) ? get<0>(doors[layer]) : get<2>(doors[layer]);
                 int door_c = (door_idx == 0) ? get<1>(doors[layer]) : get<3>(doors[layer]);

                 int prev_door_r1 = get<0>(doors[layer - 1]);
                 int prev_door_c1 = get<1>(doors[layer - 1]);
                 int prev_door_r2 = get<2>(doors[layer - 1]);
                 int prev_door_c2 = get<3>(doors[layer - 1]);

                 int new_dist1 = d + 1 + abs(door_r - prev_door_r1) + abs(door_c - prev_door_c1);
                 int new_dist2 = d + 1 + abs(door_r - prev_door_r2) + abs(door_c - prev_door_c2);

                 if (new_dist1 < dist[layer - 1][0]) {
                    dist[layer - 1][0] = new_dist1;
                    pq.emplace(new_dist1, layer - 1, 0);
                 }
                 if (new_dist2 < dist[layer - 1][1]) {
                    dist[layer - 1][1] = new_dist2;
                    pq.emplace(new_dist2, layer - 1, 1);
                 }
            }
        }

        int ans = min(dist[layer2][0] + abs(r2 - get<0>(doors[layer2])) + abs(c2 - get<1>(doors[layer2])),
                      dist[layer2][1] + abs(r2 - get<2>(doors[layer2])) + abs(c2 - get<3>(doors[layer2])));
        cout << ans << '\n';
    }

    return 0;
}

