// Function to check if a number is prime
bool isPrime(int n) {
  if (n <= 1) return false;
  for (int i = 2; i * i <= n; ++i) {
    if (n % i == 0) return false;
  }
  return true;
}

int main() {
  int n;
  cin >> n;

  vector<int> primes;
  int num = n - 1;
  while (primes.size() < 5 && num > 1) {
    if (isPrime(num)) {
      primes.push_back(num);
    }
    num--;
  }

  // Sort the primes in ascending order
  sort(primes.begin(), primes.end());

  // Print the primes
  for (size_t i = 0; i < primes.size(); ++i) {
    cout << primes[i];
    if (i < primes.size() - 1) {
      cout << " ";
    }
  }
  cout << endl;

  return 0;
}

