#include <bits/stdc++.h>
using namespace std;

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;

    vector<int> b(n), c(n);
    for (auto& x : b) cin >> x;
    for (auto& x : c) cin >> x;

    long long sum_b = accumulate(b.begin(), b.end(), 0LL);
    long long sum_c = accumulate(c.begin(), c.end(), 0LL);
    long long total = sum_b + sum_c;
    if (total % (2 * n) != 0) {
        cout << "-1\n";
        return 0;
    }

    long long sum_a = total / (2 * n);
    vector<long long> a(n);

    bool valid = true;
    for (int i = 0; i < n; ++i) {
        long long temp = b[i] + c[i] - sum_a;
        if (temp % n != 0) {
            valid = false;
            break;
        }
        a[i] = temp / n;
        if (a[i] < 0) {
            valid = false;
            break;
        }
    }

    if (!valid) {
        cout << "-1\n";
        return 0;
    }

    if (accumulate(a.begin(), a.end(), 0LL) != sum_a) {
        cout << "-1\n";
        return 0;
    }

    vector<int> s(31, 0);
    for (int i = 0; i < n; ++i) {
        for (int k = 0; k < 31; ++k) {
            if (a[i] & (1LL << k)) {
                s[k]++;
            }
        }
    }

    for (int i = 0; i < n; ++i) {
        long long exp_b = 0, exp_c = 0;
        for (int k = 0; k < 31; ++k) {
            if (a[i] & (1LL << k)) {
                exp_b += (1LL << k) * s[k];
                exp_c += (1LL << k) * n;
            } else {
                exp_c += (1LL << k) * s[k];
            }
        }
        if (exp_b != b[i] || exp_c != c[i]) {
            valid = false;
            break;
        }
    }

    if (!valid) {
        cout << "-1\n";
    } else {
        for (long long x : a) {
            cout << x << ' ';
        }
        cout << '\n';
    }

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int t;
    cin >> t;
    while (t--) {
        int n;
        cin >> n;
        vector<int> a(n + 1);
        int known = 1;

        // Find a known element (value is x after '=')
        while (true) {
            cout << "? " << known << endl;
            char res;
            cin >> res;
            if (res == '=') break;
        }

        for (int i = 1; i <= n; ++i) {
            if (i == known) {
                a[i] = i;
                continue;
            }

            // Binary search for a[i]
            int l = 1, r = n;
            while (l <= r) {
                int mid = (l + r) / 2;

                // Reset x to known's value
                char res;
                do {
                    cout << "? " << known << endl;
                    cin >> res;
                } while (res != '=');

                // Adjust x to mid by querying i
                for (int k = a[known]; k < mid; ++k) {
                    cout << "? " << i << endl;
                    cin >> res;
                }
                for (int k = a[known]; k > mid; --k) {
                    cout << "? " << i << endl;
                    cin >> res;
                }

                // Check if a[i] == mid
                cout << "? " << i << endl;
                cin >> res;
                if (res == '=') {
                    a[i] = mid;
                    break;
                } else if (res == '>') {
                    l = mid + 1;
                } else {
                    r = mid - 1;
                }
            }
        }

        cout << "! ";
        for (int i = 1; i <= n; ++i) {
            cout << a[i] << " ";
        }
        cout << endl;
    }

    return 0;
}

