#include <bits/stdc++.h>
using namespace std;

typedef long long LL;
typedef pair<int, int> Pii;
typedef pair<LL, LL> Pll;
const int INF = 0x3f3f3f3f;

int n, k;
string s, t;
set<char> st;

void solve() {
    cin >> s;

    t = "";
    st.clear();

    char mini = 'z';
    for (int i = 0; i < s.size(); ++i) {
        st.insert(s[i]);
        if (s[i] < mini) mini = s[i];
    }

    string u(st.begin(), st.end());

    if (n < k) {
        t = s;
        t += mini;
    } else {
        map<char, int> dist;
        int len = u.size();
        for (int i = 0; i < len; ++i) {
            dist[u[i]] = len-i-1;
        }

        int mark = 0;
        t = "";
        for (int i = k; i >= 0; --i) {
            if (dist[s[i]]) {
                mark = i;
                break;
            }
        }
        t.append(s.begin(), s.begin()+mark);
        t += u[mark+1];
        while (t.size() < k)
            t += mini;

    }

    cout << t << endl;
}

int main(void) {
    ios_base::sync_with_stdio(false);

    while(cin >> n >> k) {
        solve();
    }

    return 0;
}

