const int MAXN = 300005;

int tree[32 * MAXN][2];
int cnt[32 * MAXN];

int n;

int nextnode;

vector<int> a;

inline int mask(int x)
{
    return 1 << x;
}

bool contains(int s, int x)
{
    return s & mask(x);
}

void insert(int x)
{
    int curr = 0;
    cnt[curr]++;

    for (int b = 29; b >= 0; b--)
    {
        int c = contains(x, b);

        if (tree[curr][c] == -1)
        {
            tree[curr][c] = nextnode;
            nextnode++;
        }

        curr = tree[curr][c];
        cnt[curr]++;
    }
}

void erase(int x)
{
    int curr = 0;
    cnt[curr]--;

    for (int b = 29; b >= 0; b--)
    {
        int c = contains(x, b);

        curr = tree[curr][c];
        cnt[curr]--;
    }
}

int solve(int x)
{
    int curr = 0;
    int p = 0;

    for (int b = 29; b >= 0; b--)
    {
        int c = contains(x, b);

        if (tree[curr][c] != -1 && cnt[tree[curr][c]])  // goto c
        {
            p += (c ? mask(b) : 0);
            curr = tree[curr][c];
        }
        else  // goto 1-c
        {
            p += (1-c ? mask(b) : 0);
            curr = tree[curr][1-c];
        }
    }

    erase(p);
    return x ^ p;
}

int main() {
