inline ll multiply(ll a, ll b, ll m = mod) { return (a % m * b % m) % m; }
inline ll add(ll a, ll b, ll m = mod) { return (m*m + a % m + b % m) % m; }
inline ll subtract(ll a, ll b, ll m = mod) { return (m*m + a % m - b % m) % m; }

ll power(ll a, ll b, ll m = mod) {
 ll res = 1;
 while(b > 0) {
  if(b & 1) res = multiply(res,a,m);
  b >>= 1;
  a = multiply(a,a,m); 
 }
 return res % m;
} 

ll modinv(ll a, ll m = mod) {
 return power(a, m-2, m);
}

inline divide(ll a, ll b, ll m = mod) { return multiply(a,modinv(b,m),m); }

