int gcd(ll x, ll y){
	if(y == 0)
		return x;
	return gcd(y, x % y);
}
void solve(){
	int n;
	read(n);
	vt<ll> a(n);
	read(a);
	ll s = 0, ans = 0;
	rep(n)
		s = gcd(s, a[i]);
	rep(n)
		ans += a[i] / s;
	write(ans);
}
int32_t main(){
	ios::sync_with_stdio(false);
	cin.tie(0);

	int tc = 1;
	//read(tc);
	while(tc--){
		solve();
	}
}

