void sieve()
{
 fill(a.begin(),a.end(),true);
  for (int p=2; p*p<=a.size(); p++)
     {
        if (a[p] == true)
        {
            // Update all multiples of p
            for (int i=p*2; i<=a.size(); i += p)
                a[i] = false;
        }
     }
}
string toBinary(int n)
{
 string str;
 int temp;
 while(n>0)
  {
   temp=n%2;
   n/=2;
   char d=temp+'0';
   str=str+d;
  }
  reverse(str.begin(),str.end());
  return str;
}

