using namespace std;
#define ll long long
struct node{
    int c;
    int id;
};
vector<node> v;
int fnd(int number){
    for(int i=0;i<v.size();i++){
        if(v[i].id == number){
            return v[i].c;
        }
    }
}
int main(){
    ios::sync_with_stdio(false);cin.tie(0);
    int n,m;cin >> n >> m;
    for(int i=0;i<m;i++){
        int room,id;
        cin >> room >> id;
        v.push_back({room,id});
    }
    char ch;
    int number;
    vector<node> line;
    while(1){
        cin >> ch;
        if(ch=='E'){
            cin >> number;
            int cls = fnd(number);
            if(line.size()==0){
                line.push_back({cls,number});
            }
            else{
                for(int k=line.size()-1;k>=0;k--){
                    if(line[k].c==cls){
                        line.insert(line.begin()+k+1,{cls,number});
                        break;
                    }else if(k==0){
                        line.push_back({cls,number});
                    }
                }
            }
        }
        else if(ch=='D'){
            cout << line[0].id << endl;
            line.erase(line.begin());
        }
        else{
            cout << "0" << endl;
            break;
        }
    }
}

