template<typename T> T load() { T x; cin >> x; return x; }
template<typename T> vector<T> loadN(int s) { vector<T> vt(s); for(auto& el : vt) el = load<T>(); return vt; }
template<typename T> ostream& operator<<(ostream& os, const vector<T>& vt) { for(auto& el : vt) os << el ; return os; }

vector<vector<char>> loadMap(int w, int h) {
    vector<vector<char>> world;
    while(h-->0)
        world.push_back(loadN<char>(w));
    return world;
}

template<typename F>
void forEverySheep(int w, int h, vector<vector<char>>& world, F f) {
    for(int i = 0 ; i < h ; ++i) {
        for(int j = 0 ; j < w ; ++j) {
            if(world[i][j] == 'S') {
                f(i, j);
            }
        }
    }
}

void fail() {
    cout << "NO\n";
    exit(0);
}

int main() {
    ios::sync_with_stdio(false);
    auto h = load<i32>();
    auto w = load<i32>();
    auto world = loadMap(w, h);
    auto isValid = [&](int i , int j) {
        if(i < 0 || j <  0) return false;
        if(i >= h || j >= w) return false;
        if(world[i][j] == 'D') return false;
        if(world[i][j] == 'S') return false;
        return true;
    };
    auto isWolf = [&](int i, int j) {
        if(isValid(i,j))
            return  world[i][j] == 'W';
        return false;
    };
    auto placeDog = [&](int i, int j) {
        if(isValid(i, j))
            world[i][j] = 'D';
    };

    forEverySheep(w, h, world, [&](int i, int j) {
        if(isWolf(i, j + 1)) fail();
        if(isWolf(i + 1, j)) fail();
        if(isWolf(i - 1, j)) fail();
        if(isWolf(i, j -1)) fail();
        placeDog(i, j + 1);
        placeDog(i - 1, j);
        placeDog(i + 1, j);
        placeDog(i, j - 1);

    });
    cout << "YES\n";
    for(auto& line : world)
        cout << line << '\n';
    return 0;
}

