int goOrAdd(int v, int c)
{
    if (go[v][c] == -1)
    {
        p[vcnt] = v;
        ch[vcnt] = c;
        go[v][c] = vcnt++;
        if (vcnt >= MAXV)
            while (1);
    }   
    return go[v][c];
}

int getSuf(int);

int goOrSuf(int v, int c)
{
    return go[v][c] == -1 ? go[v][c] = goOrSuf(getSuf(v), c) : go[v][c];
}

int getSuf(int v)
{
    return suf[v] == -1 ? suf[v] = goOrSuf(getSuf(p[v]), ch[v]) : suf[v];
}

int getTerminal(int v)
{
    if (res[v] != -2)   
        return res[v];
    return res[v] = getTerminal(getSuf(v));
}

void dfs(int v)
{
    tin[v] = timer++;
    for (int i = cur[v]; i != -1; i = Next[i]) 
        dfs(val[i]);
    tout[v] = timer++;
}

inline bool upper(int x, int y)
{
    return tin[x] <= tin[y] && tout[x] >= tout[y];
}

bool dfsMatch(int v)
{
    used[v] = 1;
    forn(i, a[v].size())
        if (Pair[a[v][i]] == -1 || (!used[Pair[a[v][i]]] && dfsMatch(Pair[a[v][i]])))
        {
            Pair[a[v][i]] = v;
            inPair[v] = 1;
            return 1;
        }
    return 0;
}

void dfsCover(int v)
{
    used[v] = 1;
    forn(i, a[v].size())
    {
        if (!isRight[a[v][i]] && !used[Pair[a[v][i]]])
        {
            isRight[a[v][i]] = 1;
            isFree[v] = 0;
            dfsCover(Pair[a[v][i]]);
        }
    }
}

