#define fre(i,a,b,c) for(int i = a;i>=b;i-=c)
#define MAXN 1e9 + 5
using pii = pair<int,int>;
using tiii = tuple<int,int,int>;
vector<vector<char>> p(100,vector<char>(100));
int tscore = 0;


void fix(int n,int m){
    fr(i,1,n+1,1){
        fr(j,1,m+1,1){
            if(p[i][j] >= 'A' && p[i][j] <= 'Z'){
                int it = i + 1;
                bool ca = 0;
                while(it <= n && p[it][j] == '-'){
                    ca = 1;
                    swap(p[it][j], p[it-1][j]);
                    it++;
                }
                if(ca) fix(n,m);
            }
        }
    }
}


int check(int n,int m){
    vector<vector<bool>> vis(100, vector<bool>(100,false));
    int dx[4] = {1,-1,0,0}, dy[4] = {0,0,1,-1};
    fr(i,1,n+1,1){
        fr(j,1,m+1,1){
            if(p[i][j] >= 'A' && p[i][j] <= 'Z' && !vis[i][j]){
                char t = p[i][j];
                queue<pii> q;
                vector<pii> grp;
                q.push({i,j});
                vis[i][j] = 1;
                while(!q.empty()){
                    auto [x,y] = q.front(); q.pop();
                    grp.push_back({x,y});
                    fr(d,0,4,1){
                        int nx = x + dx[d], ny = y + dy[d];
                        if(nx>=1 && nx<=n && ny>=1 && ny<=m && !vis[nx][ny] && p[nx][ny]==t){
                            vis[nx][ny] = 1;
                            q.push({nx,ny});
                        }
                    }
                }
                if(grp.size()>=2){
                    for(auto &pr: grp) p[pr.first][pr.second] = '-';
                    fix(n,m);
                    return 5*grp.size() + check(n,m);
                }
            }
        }
    }
    return 0;
}


void move(int x,int y,char d,int m,int n){
    if(p[x][y]=='-'||p[x][y]=='#'){
        tscore -= 5; return;
    }
    if(d=='L'){
        if(p[x][y-1]!='-'){ tscore -= 5; return; }
        char t = p[x][y]; p[x][y]='-';
        int it=-1;
        fr(i,x,n+1,1){
            if(p[i][y-1]=='-') it = i;
            else break;
        }
        if(it==-1){ tscore -= 5; return; }
        p[it][y-1]=t;
    } else {
        if(p[x][y+1]!='-'){ tscore -= 5; return; }
        char t = p[x][y]; p[x][y]='-';
        int it=-1;
        fr(i,x,n+1,1){
            if(p[i][y+1]=='-') it = i;
            else break;
        }
        if(it==-1){ tscore -= 5; return; }
        p[it][y+1]=t;
    }
    fix(n,m);
}


int main(){
    ios::sync_with_stdio(false);
    cin.tie(NULL);
    int n,m; cin>>n>>m;
    fr(i,1,n+1,1) fr(j,1,m+1,1) cin>>p[i][j];
    int tc; cin>>tc;
    while(tc--){
        int x,y; char d;
        cin>>x>>y>>d;
        move(x+1,y+1,d,m,n);
        fix(n,m);
        tscore += check(n,m);
    }
    cout<<tscore<<"\n";
    fr(i,1,n+1,1){
        fr(j,1,m+1,1) cout<<p[i][j]<<" ";
        cout<<"\n";
    }
    return 0;
}

