        int num_steps = 0;
        num_steps = a/b + 1;
        left_bound = (num_steps-1)*b;
        right_bound = left_bound + ans;

        double x_proj;
        if (num_steps % 2 == 1) {
            //segment of form <kx,0> --> <(k+1)x,x>
            // formula for segment: y = x - left_bound
            x_proj = a - left_bound;
        }
