            // formulat for segment: y = right_bound -x
            x_proj = right_bound - a;
        }
        double diff = b - x_proj;
        if ((num_steps-1)*b == a)
            ans += diff / (num_steps - 1);
        else
            ans += diff / num_steps;
        cout << std::fixed << std::setprecision(12) << ans << '\n';
    }

    //print_timer();
