priority_queue<pair<int,pair<int,int>>> pq;
int head[200005],s[200005];


int findhead(int x){
    if(head[x]==x)return x;
    return head[x]=findhead(head[x]);
}
void unionhead(int u,int v){
    u=findhead(u), v=findhead(v);
    if(u==v)return;
    if(s[u]>s[v]){
        head[v]=u;
        s[u]=s[u]+s[v];
    }else{
        head[v]=u;
        s[v]=s[v]+s[u];
    }


}




