        pq.push({z-1,{x,y}});


    }
    long long total=0;


    while(pq.empty()==0){
        int w=pq.top().first;
        int u=pq.top().second.first;
        int v=pq.top().second.second;
        pq.pop();
        if(findhead(u)==findhead(v)) continue;
        total=total+w;
        unionhead(u,v);
