void runcase(){
    int n,k;
    cin >> n >> k;
    int g=0;
    for(int i=0;i<n;i++){
        int x;
        cin >> x;
        g=gcd(g,x);
    }
    if(n==1){
        int ans=k-1;
        if(g<=ans)ans++;
        cout << ans << "\n";
        return;
    }
    if(g==0){
        cout << k << "\n";
        return;
    }
    int ans=k-1;
    int cur=0,cnt=0;
    while(cnt<n&&cur<=ans){
        ans++;
        cur+=g;
        cnt++;
    }
    cout << ans << "\n";
}

int main(){
    cin.tie(nullptr)->sync_with_stdio(false);
    int t(1);
    cin >> t;
    while(t--)runcase();
}

