bool symbolInRange(wchar_t wc, const std::string RANGE)
{
 for(auto i = RANGE.begin(); i != RANGE.end(); ++i)
 {
  if(wc == *i) return true;
 }

 return false;
}

bool checkLogin(std::string s, std::vector<std::string> &t)
{
 for(auto i = t.begin(); i != t.end(); ++i)
 {
  if((*i).size() != s.size()) continue;
  for(int j = 0; j < s.size(); ++j)
  {
   bool a = symbolInRange((wchar_t)(*i)[j],RANGE_1) && symbolInRange((wchar_t)s[j],RANGE_1);
   bool b = symbolInRange((wchar_t)(*i)[j],RANGE_2) && symbolInRange((wchar_t)s[j],RANGE_2);
   bool c = (wchar_t)toupper((*i)[j]) == (wchar_t)toupper(s[j]) && 
         (wchar_t)tolower((*i)[j]) == (wchar_t)tolower(s[j]);
   if(a || b || c)
   {
    if(j == s.size() - 1)
    {
     std::cout << *i << std::endl;
     return false;
    }
    continue;
   }
   else break;
  }
  //std::cout << std::endl;
 }

 return true;
}

