const int maxn=3*1e5+1;
int a[maxn],f[maxn];int n;
void init(int n)
{
 for(int i=1;i<=n;i++)
  for(int j=1;i*j<=n;j++)
   f[i*j]++;
}
void update(int i,int x){
     while(i<=n){
        a[i]+=x;
        i+=i&-i;
     }
}
int query(int x){
    int sum=0;
    while(x>0){
      sum+=a[x];
      x-=x&-x;
    }
    return sum;
}
void replace(int l,int r)
{
 for(int i=l;i<=r;i++)
 {
  int tmp=f[query(i)-query(i-1)]-(query(i)-query(i-1));
  update(i,tmp);
 }
}
