#include <bits/stdc++.h>
#define MAXN 1024

using namespace std;

typedef long long ll;
typedef pair<int, int> ii;
typedef pair<int, ii> iii;

int t[MAXN], s[MAXN], f[MAXN], v[MAXN];

ii findInter(int a, int b, int x, int y) {
    if (a > b) swap(a, b);
    if (x > y) swap(x, y);

    if (a <= x) {
        if (b < x) return ii(-1, -1);
        else return ii(x, min(b, y));
    }
    else {
        if (y < a) return ii(-1, -1);
        else return ii(a, min(y, b));
    }
}

int main() {
    int n; scanf("%d", &n);
    for(int i=0; i<n; i++) {
        scanf("%d%d%d", &t[i], &s[i], &f[i]);
        v[i] = s[i]>f[i]?-1:1;
    }

    int meet, tin1, tout1, tin2, tout2;
    ii inter;
    for(int i=0; i<n; i++) {
        meet = 0;
        for(int j=0; j<n; j++) {
            if (i==j) continue;
            if (v[i] == v[j]) {
                if (t[i] == t[j] && s[i] == s[j]) {
                    meet++;
                }
                else {
                    inter = findInter(s[i], f[i], s[j], f[j]);

                    if (inter == ii(-1, -1)) continue;

                    tin1 = (inter.first - s[i])*v[i];
                    tout1 = (inter.second - s[i])*v[i];
                    if (tin1 > tout1) swap(tin1, tout1);

                    tin2 = (inter.first - s[j])*v[j];
                    tout2 = (inter.second - s[j])*v[j];
                    if (tin2 > tout2) swap(tin2, tout2);

                    if (tin1 >= tin2 && tout1 >= tin2) meet++;
                    else if (tin2 >= tin1 && tout2 >= tin1) meet++;
                }
            }
            else {
                inter = findInter(s[i], f[i], s[j], f[j]);

                if (inter == ii(-1, -1)) continue;

                tin1 = (inter.first - s[i])*v[i];
                tout1 = (inter.second - s[i])*v[i];
                if (tin1 > tout1) swap(tin1, tout1);

                tin2 = (inter.first - s[j])*v[j];
                tout2 = (inter.second - s[j])*v[j];
                if (tin2 > tout2) swap(tin2, tout2);

                if (tin1 >= tin2 && tout1 >= tin2) meet++;
                else if (tin2 >= tin1 && tout2 >= tin1) meet++;
            }
        }
        printf("%d ", meet);
    }

    return 0;
}

