#define x first            // Change word 'first' to word 'x'
#define y second           // Change word 'second' to word 'y'
#define INF 9e18           // Set INF be large number as infinity number


using namespace std;


using point = pair<double, double>; // Set point is pair of double
                                    // first of element is x
                                    // second of element is y


double dst(point p1, point p2)
{ 
  // find the euclidean distance in 2D space
  double dx = p1.x - p2.x;    // diff x
  double dy = p1.y - p2.y;    // diff y
  return sqrt(dx*dx + dy*dy); // formular of euclidean
}


bool cmp_yaxis(point p1, point p2)
{
  // compare function for sorting point by y-axis
  return p1.y > p2.y;
}


/* Definition function of Problem
   + ClosestPair(xsort:Array[point], ysort:Array[point]): double 
   + return type is distance of closest pair of point            */
double ClosestPair(vector<point> xsort, vector<point> ysort);


int main()
{
  int w;
  cin>>w;
  for(int PPAP = 0;PPAP<w;PPAP++){
    /*  Input sector */

  // Input `N` as a Number of Points in 2D space
  int n,r,d;
  cin>>n>>r>>d;


  // Create 2 List of `N` points
  vector<point> xsort(n), ysort(n);
  for (int i = 0; i < n; ++i) {
    double x, y;
    //printf(" At point[%d] has (x, y): ", i);


    // Input information of a point `i` : x y
    scanf("%lf%lf", &x, &y);


    // Store point `i` to xsort and ysort
    xsort[i] = ysort[i] = point(x, y);
  }


  /*  Finding closest distance sector of any 2 points */


  // Sort point `xsort` by x-axis (defualt of sorting with std::pair<> )
  sort(xsort.begin(), xsort.end());


  // Sort each point in list strip by y-axis (using compare function by y-axis)
  sort(ysort.begin(), ysort.end(), cmp_yaxis);


  // Finding `answer` of closest pair of point in list `p` 
  // ClosestPair(xsort:Array[point], ysort:Array[point])
  double answer = ClosestPair(xsort, ysort);


  /* Output of Problem */
    if(abs(answer) < r+r+d){
        cout<<"N\n";
    }
    else{
        cout<<"Y\n";
    }
 }
  return 0;
}


// Declaration of function ClosestPair()
double ClosestPair(vector<point> xsort, vector<point> ysort)
{
  // let n is number of points to calculate
  int n = (int) xsort.size();


  // Base Case #1: No point to build any distance
  if (n <= 1) return INF;


  // Base Case #2: There is one pair to find distance
  if (n == 2) return dst(xsort[0], xsort[1]);


  /* Recurrence Case (Idea concept): 
       + Divide points into 2 side(Left side and Right side)
       + Combine them with strip area and box scanning
  */


  /*  Divide Section  */


  int mid = n/2;                // find the `mid` of Index  `L` and `R` 
  point midPoint = xsort[mid];  // remember the `midPoint`


  /* technique for dividing points */


  // xL = xsort[0...mid]
  vector<point> xL(xsort.begin(), xsort.begin() + mid + 1);


  // xR = xsort[mid+1...n-1]
  vector<point> xR(xsort.begin() + mid + 1, xsort.end());

  vector<point> yL, yR;      // let yL and yR are the empty list
  for (auto p : ysort) {     // scan each point `p` from `ysort`
    if (p.x <= midPoint.x)   // if point `p` stay in left side 
      yL.push_back(p);       // append `p` to `yL` list
    else                     // but doesn't
      yR.push_back(p);       // append `p` to `yR` list
  }


  double dL = ClosestPair(xL, yL);      // recursivly left side of points
  double dR = ClosestPair(xR, yR);      // recursivly right side of points


  double d = min(dL, dR);               // Let `d` is shortest of closest distance


  /*  Combine Section  */


  // Let `strip` be a list of point that has possible better answer
  vector<point> strip;
  for (auto p : ysort) {                // scan each point `p` in `ysort` list
    if (abs(p.x - midPoint.x) < d) {    // if point `p` stay in strip area
      strip.push_back(p);               // then append `p` to `strip` list
    }
  }


  // start box scanning
  for (int cur = 0; cur < (int) strip.size(); ++cur) {
    // let `cur` be the index of point `i`
    // let `nxt` be the next index of each point `i`
    for (int nxt = cur + 1; nxt < (int) strip.size() ; ++nxt) {


      // this point will guarantee that loop will iterate <= 8 times
      if (abs(strip[nxt].y - strip[cur].y) >= d) 
        break; 


      // finding the minimum value
      d = min (d, dst(strip[cur], strip[nxt]));
    }

  }


  // return value of closest distance of points in index `L` to `R`
  return d;
}

