bool isletter(char a)
{
    if(a>='A'&&a<='Z')
        return true;
    else if(a>='a'||a<='z')
        return true;
    else
        return false;
}
bool issame(char a,char b)
{
    if(a>'Z')
    {
        if(b>'Z')
        {
            if(a==b)
                return true;
            else
                return false;
        }
        else
        {
            if(a==b-'A'+'a')
                return true;
            else
                return false;
        }
    }
    else
    {
        if(b>'Z')
        {
            if(a==b-'a'+'A')
                return true;
            else
                return false;
        }
        else
        {
            if(a==b)
                return true;
            else
                return false;
        }
    }
}
bool issame2(char a[],char b[])
{
    if(strlen(a)!=strlen(b))
        return false;
    for(int i=0;i<strlen(a);i++)
    {
        if(isletter(a[i]))
        {
            if(!issame(a[i],b[i]))
                return false;
        }
        else
        {
            if(a[i]!=b[i])
                return false;
        }
    }
    return true;
}
