typedef long long lld;

lld n, k, A, B;

void work() {
    cin >> n >> k >> A >> B;

    if (k == 1) {
        cout << (n - 1) * A << endl;
        return ;
    }
    if (n == 1) {
        cout << 0 << endl;
        return ;
    }
    lld ans = 1LL<<62;
    for (int cnt = 0; cnt <= 32; ++cnt) {
        lld now = n, s = 0;
        int c = 0;
        while (now != 1) {
            if (now % k == 0 && c < cnt) {
                s += B;
                now /= k;
                c ++;
            } else if (c == cnt || now < k) {
                s += (now - 1) * A;
                now = 1;
            } else {
                s += (now % k) * A;
                now -= (now % k);
            }
        }
        if (ans > s) {
            ans = s;
        }
    }
    cout << ans << endl;
    return ;
}


int main() {
    work();
    return 0;
}

